const BASE_URL = '/api';

const getProducts = async () => {
  const response = await fetch(`${BASE_URL}/products`);
  return await response.json();
};

const getCart = async () => {
  const response = await fetch(`${BASE_URL}/cart/user/123456`); // Replace with actual user ID
  return await response.json();
};

const addToCart = async (productId, quantity) => {
  const response = await fetch(`${BASE_URL}/cart`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({ productId, quantity, userId: '123456' }) // Replace with actual user ID
  });
  return await response.json();
};

const removeFromCart = async (itemId) => {
  await fetch(`${BASE_URL}/cart/${itemId}`, {
    method: 'DELETE'
  });
};

export default {
  getProducts,
  getCart,
  addToCart,
  removeFromCart
};
