const BASE_URL = '/api';

// Fonction utilitaire pour g√©rer les r√©ponses
const handleResponse = async (response) => {
  if (!response.ok) {
    // On r√©cup√®re le message d'erreur du serveur si disponible
    try {
      const errorData = await response.json();
      throw new Error(errorData.message || `Erreur HTTP: ${response.status}`);
    } catch (e) {
      throw new Error(`Erreur HTTP: ${response.status}`);
    }
  }
  return response.json();
};

// Fonction utilitaire pour les requ√™tes fetch
const fetchWithConfig = async (endpoint, options = {}) => {
  const defaultHeaders = {
    'Content-Type': 'application/json',
    // Ajoutez ici d'autres headers par d√©faut si n√©cessaire
  };

  const config = {
    ...options,
    headers: {
      ...defaultHeaders,
      ...options.headers,
    },
  };

  try {
    const response = await fetch(`${BASE_URL}${endpoint}`, config);
    return handleResponse(response);
  } catch (error) {
    console.error(`Erreur API (${endpoint}):`, error);
    throw new Error(error.message || 'Erreur de connexion au serveur');
  }
};

const api = {
  getProducts: async () => {
    console.log('üì¶ R√©cup√©ration des produits...');
    try {
      // Modification du chemin
      const data = await fetchWithConfig('/products', {
        method: 'GET',  // Explicite la m√©thode
      });
      console.log('‚úÖ Produits re√ßus:', data);
      return data;
    } catch (error) {
      console.error('‚ùå Erreur products:', error);
      throw error;
    }
  },

  getProductById: (productId) => 
    fetchWithConfig(`products/${productId}`),

  // Panier
  getCart: (userId = '123456') => 
    fetchWithConfig(`/cart/user/${userId}`),

  addToCart: (productId, quantity, userId = '123456') => 
    fetchWithConfig('/cart', {
      method: 'POST',
      body: JSON.stringify({ productId, quantity, userId })
    }),

  removeFromCart: (itemId) => 
    fetchWithConfig(`/cart/${itemId}`, {
      method: 'DELETE'
    }),

  // Fonction utilitaire pour v√©rifier la connexion √† l'API
  checkConnection: async () => {
    try {
      console.log('Tentative de connexion √† l\'API...');
      const response = await fetch(`${BASE_URL}/products`);
      console.log('Status:', response.status);
      const data = await response.json();
      console.log('Donn√©es re√ßues:', data);
      return response.ok;
    } catch (error) {
      console.error('Erreur de connexion:', error);
      return false;
    }
  },
};

export default api;
