// src/utils/api.js
const BASE_URL = '/api';

const handleResponse = async (response) => {
  if (!response.ok) {
    const error = await response.json().catch(() => ({}));
    throw new Error(error.message || `Erreur HTTP ${response.status}`);
  }
  return response.json();
};

const fetchWithConfig = async (endpoint, options = {}) => {
  const token = localStorage.getItem('token');
  const defaultHeaders = {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    ...(token && { 'Authorization': `Bearer ${token}` })
  };

  const config = {
    ...options,
    headers: {
      ...defaultHeaders,
      ...options.headers,
    },
  };

  const fullUrl = endpoint.startsWith('/api') 
    ? endpoint 
    : `${BASE_URL}${endpoint.startsWith('/') ? endpoint : `/${endpoint}`}`;
  
  console.log(`ðŸ” URL finale: ${fullUrl}`);
  
  try {
    console.log(`ðŸ“¤ Envoi requÃªte vers: ${fullUrl}`);
    const response = await fetch(fullUrl, config);
    console.log(`ðŸ“¥ RÃ©ponse reÃ§ue: ${response.status}`);
    return handleResponse(response);
  } catch (error) {
    console.error(`âŒ Erreur API (${fullUrl}):`, error);
    throw error;
  }
};

const api = {
  login: async (email, password) => {
    return fetchWithConfig('/auth/login', {
      method: 'POST',
      body: JSON.stringify({ email, password })
    });
  },
  register: async (userData) => {
    return fetchWithConfig('/auth/register', {
      method: 'POST',
      body: JSON.stringify(userData)
    });
  },
  // ... autres fonctions
};

export default api;
