// src/components/Navbar.js
import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../hooks/useCart';
import { AuthContext } from '../hooks/useAuth';
import LoginModal from './LoginModal';
import RegisterModal from './RegisterModal';

const Navbar = () => {
  const { itemCount } = useContext(CartContext);
  const { user, logout } = useContext(AuthContext);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [showRegisterModal, setShowRegisterModal] = useState(false);

  return (
    <nav>
      <ul>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/cart">Panier ({itemCount})</Link></li>
        {user ? (
          <>
            <li>Bienvenue, {user.name}</li>
            <li><button onClick={logout}>DÃ©connexion</button></li>
          </>
        ) : (
          <>
            <li><button onClick={() => setShowLoginModal(true)}>Connexion</button></li>
            <li><button onClick={() => setShowRegisterModal(true)}>Inscription</button></li>
          </>
        )}
      </ul>
      {showLoginModal && <LoginModal onClose={() => setShowLoginModal(false)} />}
      {showRegisterModal && <RegisterModal onClose={() => setShowRegisterModal(false)} />}
    </nav>
  );
};

export default Navbar;
