import React, { useEffect } from 'react'; // Ajout de useEffect dans l'import
import useProducts from '../hooks/useProduct';
import api from '../utils/api'; // Ajout de l'import de api

const ProductList = () => {
  const { products, loading, error } = useProducts();

  useEffect(() => {
    api.checkConnection()
      .then(isConnected => {
        console.log('Statut de la connexion:', isConnected);
      });
  }, []);

  if (loading) return <div>Chargement...</div>;
  if (error) return <div>Erreur: {error}</div>;
  if (!products || !Array.isArray(products)) {
    console.error('Products data:', products);
    return <div>Aucun produit disponible</div>;
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {products.map((product) => (
        <div key={product._id} className="border p-4 rounded-lg">
          <img
            src={product.image}
            alt={product.name}
            className="w-full h-48 object-cover"
          />
          <h2 className="text-xl font-bold mt-2">{product.name}</h2>
          <p className="text-gray-600">{product.description}</p>
          <p className="text-lg font-bold mt-2">{product.price}â‚¬</p>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
