import React, { useState, useEffect, createContext } from 'react';
import api from '../utils/api';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  const addToCart = async (product, quantity) => {
    const cartItem = await api.addToCart(product._id, quantity);
    setCart([...cart, cartItem]);
    setTotal(total + product.price * quantity);
  };

  const removeFromCart = async (itemId) => {
    await api.removeFromCart(itemId);
    const updatedCart = cart.filter((item) => item._id !== itemId);
    setCart(updatedCart);
    setTotal(updatedCart.reduce((acc, item) => acc + item.product.price * item.quantity, 0));
  };

  return (
    <CartContext.Provider value={{ cart, total, addToCart, removeFromCart }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  const context = React.useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
