import { useState, useEffect } from 'react';
import api from '../utils/api';

export const CartContext = React.createContext();

const useCart = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const fetchCart = async () => {
      const cartItems = await api.getCart();
      setCart(cartItems);
      setTotal(cartItems.reduce((acc, item) => acc + item.product.price * item.quantity, 0));
    };
    fetchCart();
  }, []);

  const addToCart = async (product, quantity) => {
    const cartItem = await api.addToCart(product._id, quantity);
    setCart([...cart, cartItem]);
    setTotal(total + product.price * quantity);
  };

  const removeFromCart = async (itemId) => {
    await api.removeFromCart(itemId);
    const updatedCart = cart.filter((item) => item._id !== itemId);
    setCart(updatedCart);
    setTotal(total - updatedCart.reduce((acc, item) => acc + item.product.price * item.quantity, 0));
  };

  return { cart, total, addToCart, removeFromCart };
};


export const CartProvider = ({ children }) => {
  // Cart-related logic
  return (
    <CartContext.Provider value={{ /* cart-related values */ }}>
      {children}
    </CartContext.Provider>
  );
};

export default useCart;
