import React, { createContext, useState, useContext } from 'react';
import api from '../utils/api';

export const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [itemCount, setItemCount] = useState(0);

  const calculateTotal = (items) => {
    return items.reduce((sum, item) => sum + (item.product.price * item.quantity), 0);
  };

  const updateItemCount = (items) => {
    return items.reduce((count, item) => count + item.quantity, 0);
  };

  const addToCart = async (product, quantity) => {
    try {
      console.log('üõí Ajout au panier:', product.name);
      const response = await api.addToCart(product._id, quantity);
      
      if (response) {
        const updatedCart = [...cart, { 
          product, 
          quantity,
          _id: response._id 
        }];
        setCart(updatedCart);
        setTotal(calculateTotal(updatedCart));
        setItemCount(updateItemCount(updatedCart));
        console.log('‚úÖ Produit ajout√© au panier');
      }
    } catch (error) {
      console.error('‚ùå Erreur lors de l\'ajout au panier:', error);
    }
  };

  return (
    <CartContext.Provider value={{ 
      cart, 
      total, 
      itemCount,
      addToCart, 
      removeFromCart 
    }}>
      {children}
    </CartContext.Provider>
  );
};
