const BASE_URL = '/api';

// Fonction utilitaire pour gérer les réponses
const handleResponse = async (response) => {
  if (!response.ok) {
    // On récupère le message d'erreur du serveur si disponible
    try {
      const errorData = await response.json();
      throw new Error(errorData.message || `Erreur HTTP: ${response.status}`);
    } catch (e) {
      throw new Error(`Erreur HTTP: ${response.status}`);
    }
  }
  return response.json();
};

// Fonction utilitaire pour les requêtes fetch
const fetchWithConfig = async (endpoint, options = {}) => {
  const defaultHeaders = {
    'Content-Type': 'application/json',
    // Ajoutez ici d'autres headers par défaut si nécessaire
  };

  const config = {
    ...options,
    headers: {
      ...defaultHeaders,
      ...options.headers,
    },
  };

  try {
    const response = await fetch(`${BASE_URL}${endpoint}`, config);
    return handleResponse(response);
  } catch (error) {
    console.error(`Erreur API (${endpoint}):`, error);
    throw new Error(error.message || 'Erreur de connexion au serveur');
  }
};

const api = {
  // Produits
  getProducts: () => 
    fetchWithConfig('/products'),

  getProductById: (productId) => 
    fetchWithConfig(`/products/${productId}`),

  // Panier
  getCart: (userId = '123456') => 
    fetchWithConfig(`/cart/user/${userId}`),

  addToCart: (productId, quantity, userId = '123456') => 
    fetchWithConfig('/cart', {
      method: 'POST',
      body: JSON.stringify({ productId, quantity, userId })
    }),

  removeFromCart: (itemId) => 
    fetchWithConfig(`/cart/${itemId}`, {
      method: 'DELETE'
    }),

  // Fonction utilitaire pour vérifier la connexion à l'API
  checkConnection: async () => {
    try {
      const response = await fetch(`${BASE_URL}/health`);
      return response.ok;
    } catch (error) {
      console.error('Erreur de connexion à l\'API:', error);
      return false;
    }
  }
};

export default api;
