// Configuration de base
const BASE_URL = '/api';

// Fonction utilitaire pour g√©rer les r√©ponses
const handleResponse = async (response) => {
  if (!response.ok) {
    const error = await response.json().catch(() => ({}));
    throw new Error(error.message || `Erreur HTTP ${response.status}`);
  }
  return response.json();
};

// Configuration des requ√™tes
const fetchWithConfig = async (endpoint, options = {}) => {
  // Retirer le pr√©fixe /api s'il est d√©j√† pr√©sent
  const cleanEndpoint = endpoint.replace(/^\/api/, '');
  const fullUrl = `${BASE_URL}${cleanEndpoint.startsWith('/') ? cleanEndpoint : `/${cleanEndpoint}`}`;
  
  console.log(`üîç URL finale: ${fullUrl}`);
  
  const defaultHeaders = {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  };

  const config = {
    ...options,
    headers: {
      ...defaultHeaders,
      ...options.headers,
    },
  };

  try {
    console.log(`üì§ Envoi requ√™te vers: ${fullUrl}`);
    const response = await fetch(fullUrl, config);
    console.log(`üì• R√©ponse re√ßue: ${response.status}`);
    return handleResponse(response);
  } catch (error) {
    console.error(`‚ùå Erreur API (${fullUrl}):`, error);
    throw error;
  }
};

const api = {
  // Produits
  getProducts: async () => {
    console.log('üì¶ R√©cup√©ration des produits...');
    try {
      const data = await fetchWithConfig('/products', {
        method: 'GET'
      });
      console.log('‚úÖ Produits re√ßus:', data);
      return data;
    } catch (error) {
      console.error('‚ùå Erreur products:', error);
      throw error;
    }
  },

  getProductById: async (id) => {
    try {
      return await fetchWithConfig(`/products/${id}`, {
        method: 'GET'
      });
    } catch (error) {
      console.error(`‚ùå Erreur r√©cup√©ration produit ${id}:`, error);
      throw error;
    }
  },

  // Panier
  getCart: async (userId) => {
    try {
      return await fetchWithConfig(`/cart/user/${userId}`, {
        method: 'GET'
      });
    } catch (error) {
      console.error('‚ùå Erreur r√©cup√©ration panier:', error);
      throw error;
    }
  },

  // Test de connexion
  checkConnection: async () => {
    try {
      console.log('üîå Test de connexion API...');
      const response = await fetchWithConfig('/products', {
        method: 'GET'
      });
      return !!response;
    } catch (error) {
      console.error('‚ùå Erreur de connexion:', error);
      return false;
    }
  }
};

export default api;
