// src/hooks/useCart.js
import React, { createContext, useState, useContext } from 'react';
import api from '../utils/api';

// Créer et exporter le contexte
export const CartContext = createContext(null);

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [itemCount, setItemCount] = useState(0);

  const calculateTotal = (items) => {
    return items.reduce((sum, item) => sum + (item.product.price * item.quantity), 0);
  };

  const updateItemCount = (items) => {
    return items.reduce((count, item) => count + item.quantity, 0);
  };

  const addToCart = async (product, quantity) => {
    try {
      const response = await api.addToCart(product._id, quantity);
      if (response) {
        const updatedCart = [...cart, { product, quantity, _id: response._id }];
        setCart(updatedCart);
        setTotal(calculateTotal(updatedCart));
        setItemCount(updateItemCount(updatedCart));
      }
    } catch (error) {
      console.error('❌ Erreur lors de l\'ajout au panier:', error);
    }
  };

  const removeFromCart = async (itemId) => {
    try {
      await api.removeFromCart(itemId);
      const updatedCart = cart.filter(item => item._id !== itemId);
      setCart(updatedCart);
      setTotal(calculateTotal(updatedCart));
      setItemCount(updateItemCount(updatedCart));
    } catch (error) {
      console.error('❌ Erreur lors de la suppression:', error);
    }
  };

  return (
    <CartContext.Provider value={{ 
      cart, 
      total, 
      itemCount,
      addToCart, 
      removeFromCart 
    }}>
      {children}
    </CartContext.Provider>
  );
};

// Hook personnalisé pour utiliser le contexte
export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart doit être utilisé dans un CartProvider');
  }
  return context;
};
